import{_ as i,c as a,a8 as t,o as s}from"./chunks/framework.zajLutfY.js";const o="/assets/tab-group.xUgjRAC9.png",r="/assets/tab-group-new.87zRrKoN.png",e="/assets/tab-group-edit2.C489ywj-.png",n="/assets/tab-group-edit2-extension.BWbQawvT.png",p="/assets/tab-group-edit.BYzn45YP.png",u="/assets/tab-group-setting.ChXw2Z8w.png",q=JSON.parse('{"title":"标签页分组","description":"","frontmatter":{},"headers":[],"relativePath":"quick-tips/tab-group.md","filePath":"quick-tips/tab-group.md"}'),c={name:"quick-tips/tab-group.md"};function g(d,l,m,_,h,b){return s(),a("div",null,l[0]||(l[0]=[t('<h1 id="标签页分组" tabindex="-1">标签页分组 <a class="header-anchor" href="#标签页分组" aria-label="Permalink to &quot;标签页分组&quot;">​</a></h1><p>当我们在开发时，经常需要打开多个标签页，为了方便管理，可以给标签页分组，方便切换和查看（<span class="red">具体效果图如下，谁用谁知道，早用早受惠</span>）。</p><p><img src="'+o+'" alt="标签页分组" loading="lazy"></p><h2 id="操作步骤" tabindex="-1">操作步骤 <a class="header-anchor" href="#操作步骤" aria-label="Permalink to &quot;操作步骤&quot;">​</a></h2><ul><li>点击：右键点击某个标签页。</li><li>选择分组： <ul><li>选择【向群组中添加标签页】。</li><li>接着点按【新建组】或【选择现有分组】。 <ul><li>如果选择【新建组】，可给分组定义分组名和分组标签颜色。</li><li><img src="'+r+'" alt="选择分组" loading="lazy"></li></ul></li></ul></li><li>管理分组： <ul><li>书签栏 <ul><li>书签栏【具体分组】右键可支持：分组移至新窗口、取消固定分组、删除分组、打开分组中具体标签页。</li><li><img src="'+e+'" alt="管理分组-书签栏具体分组" loading="lazy"></li><li>书签栏【分组右侧扩展项】点击可支持：创建新标签页分组、操作具体分组（分组移至新窗口、固定分组、取消固定分组、删除分组）。</li><li><img src="'+n+'" alt="管理分组-书签栏分组右侧扩展项" loading="lazy"></li></ul></li><li>书签 <ul><li>右键支持：修改分组名称、修改分组颜色、添加标签页、分组移至新窗口、取消分组、关闭分组、删除分组。</li><li><img src="'+p+'" alt="管理分组-书签" loading="lazy"></li></ul></li></ul></li></ul><h2 id="开关设置" tabindex="-1">开关设置 <a class="header-anchor" href="#开关设置" aria-label="Permalink to &quot;开关设置&quot;">​</a></h2><ul><li>分组展示开关 <ul><li>浏览器右上角点击【...】，然后选中【设置】。</li><li>左侧菜单选中【外观】。</li><li>打开【在书签栏中显示标签页分组】开关。</li><li>【标签页分组】就<strong>展示</strong>在书签栏左侧了。</li></ul></li><li>分组自动固定开关 <ul><li>浏览器右上角点击【...】，然后选中【设置】。</li><li>左侧菜单选中【外观】。</li><li>打开【自动将在任何设备上创建的新标签页分组固定到书签栏】开关。</li><li>每次分组都<strong>固定</strong>在书签栏左侧了。</li></ul></li><li><img src="'+u+'" alt="标签页分组开关设置" loading="lazy"></li></ul>',7)]))}const k=i(c,[["render",g]]);export{q as __pageData,k as default};
